const { INTEGER, STRING, DATE } = require('sequelize').DataTypes

module.exports = Object.freeze({
  id(name = 'id') {
    return {
      [name]: this.increments({ primaryKey: true })
    }
  },

  increments(options) {
    return Object.assign({
      type: INTEGER,
      allowNull: false,
      autoIncrement: true,
    }, options)
  },

  foreignKey(options) {
    return Object.assign({
      type: INTEGER,
      allowNull: false
    }, options)
  },

  string(options) {
    return Object.assign({
      type: STRING,
      allowNull: false,
    }, options)
  },

  timestamp(options) {
    return Object.assign({
      type: DATE,
      allowNull: false
    }, options)
  },

  timestamps(options) {
    return {
      createdAt: this.timestamp(),
      updatedAt: this.timestamp()
    }
  }
})
