const { DataTypes } = require('sequelize')

module.exports = function(...args) {

  if(args.length < 1) {
    args = [ 'createdAt', 'updatedAt' ]
  }

  const ret = {}

  if(args.includes('createdAt')) {
    Object.assign(ret, {
      createdAt: {
        type: DataTypes.DATE,
        allowNull: false,
        validate: {
          isDate: true,
          notEmpty: true,
        }
      }
    })
  }

  if(args.includes('updatedAt')) {
    Object.assign(ret, {
      createdAt: {
        type: DataTypes.DATE,
        allowNull: false,
        validate: {
          isDate: true,
          notEmpty: true,
        }
      }
    })
  }

  return ret
}
